const textarea=document.getElementById("sussycode"),wordCount=document.getElementById("charCount");function charCount(e){const t=e.value.length,n=e.value.match(/\bsus\b/gi)||[];wordCount.innerText=`${t} Characters - ${n.length} Susses`}function autoResize(e){}textarea.addEventListener("input",()=>{autoResize(textarea),charCount(textarea)});const translateDictionary="><+-[].,".split("");function translateCode(){const e=document.getElementById("sussycode"),t=e.value.replace(/\s/g,"").split("").map((e,t)=>"sus ".repeat(translateDictionary.findIndex(t=>t===e)+1).trim()).filter(e=>e).join("\n").trim();e.value=`sussy\n${t}\nsussy`,autoResize(textarea),charCount(textarea)}async function loadExample(e){document.getElementById("sussycode").value=await fetch("./examples/"+e).then(e=>e.text())}function copyCode(){document.getElementById("sussycode").select(),document.execCommand("copy"),window.getSelection().removeAllRanges()}const files=[{name:"hello_world.sus",description:'Outputs "Hello World!"'},{name:"fib.sus",description:"Outputs the fibonacci sequence"},{name:"e.sus",description:"Outputs the euler number"},{name:"golden.sus",description:"Outputs the golden ratio"},{name:"squares.sus",description:"Outputs the squares of the numbers from 0 to infinity"},{name:"echo.sus",description:"Outputs the input"}];function exLoad(){const e=document.getElementById("examples"),t=files.sort((e,t)=>t.name.length-e.name.length).map(e=>`<button onclick="loadExample('${e.name}')">${e.name}</button> - <span>${e.description}</span>`).join("<br />");e.innerHTML=t}exLoad();
